INSERT INTO section VALUES("194","213","Prob Solv & Comp Prog","<p>text</p>\n","<table border=\"1\" cellpadding=\"1\" cellspacing=\"1\" style=\"height:29px; width:500px\">\n	<tbody>\n		<tr>\n			<td>\n			<ul>\n				<li>Course Website</li>\n			</ul>\n			</td>\n			<td>https://gannon.blackboard.com</td>\n		</tr>\n	</tbody>\n</table>\n\n<p>&nbsp;</p>\n","<table>\n	<tbody>\n		<tr>\n			<td style=\"vertical-align:top\">\n			<table border=\"1\">\n				<tbody>\n					<tr>\n						<th>Grade Componenent</th>\n						<th>Weight</th>\n					</tr>\n					<tr>\n						<th>Assignments:</th>\n						<th>&nbsp;</th>\n					</tr>\n					<tr>\n						<th>Examinations/Quizzes:</th>\n						<th>&nbsp;</th>\n					</tr>\n					<tr>\n						<th>Project:</th>\n						<th>&nbsp;</th>\n					</tr>\n				</tbody>\n			</table>\n			</td>\n			<td><!-- Grading criteria-->\n			<table border=\"1\">\n				<tbody>\n					<tr>\n						<th>Course Score</th>\n						<th>Undergraduate Grade</th>\n					</tr>\n					<tr>\n						<td>&gt;=98</td>\n						<td>A+</td>\n					</tr>\n					<tr>\n						<td>93-97</td>\n						<td>A</td>\n					</tr>\n					<tr>\n						<td>90-92</td>\n						<td>A-</td>\n					</tr>\n					<tr>\n						<td>87-89</td>\n						<td>B+</td>\n					</tr>\n					<tr>\n						<td>84-86</td>\n						<td>B</td>\n					</tr>\n					<tr>\n						<td>80-83</td>\n						<td>B-</td>\n					</tr>\n					<tr>\n						<td>75-79</td>\n						<td>C+</td>\n					</tr>\n					<tr>\n						<td>70-74</td>\n						<td>C</td>\n					</tr>\n					<tr>\n						<td>65-69</td>\n						<td>C-</td>\n					</tr>\n					<tr>\n						<td>60-64</td>\n						<td>D</td>\n					</tr>\n					<tr>\n						<td>60</td>\n						<td>F</td>\n					</tr>\n				</tbody>\n			</table>\n			</td>\n		</tr>\n	</tbody>\n</table>\n","<p>pol</p>\n","<p><u>Attendance -</u></p>\n\n<ul>\n	<li>I expect students to attend and actively participate.</li>\n	<li>Three <strong>inadequately excused absences</stro